import { ref } from "vue";
import { useStore } from "vuex";
let showNav = ref(false);

const store = useStore();

const toggleNav = () => {
  showNav.value = !showNav.value;
  store.dispatch("playSong");
};

const toggleNavWithTimeout = () => {
  setTimeout(() => {
    showNav.value = !showNav.value;
  }, 500);
  store.dispatch("playSong");
};
</script>

